//JavaScript
window.datasources = datasources;
//Widget Configuration Settings
window.config = {
    datasourceAppSettings: "application_properties",
    datasourceMQTTPolygons: "geoFenceManager",
    datasourceListPolygons: "geoFencesList",
    datasourceMarkerLocations: "location",
    datasourceNameDevices: "devicesList",
    targetContainer: "map"
};

///******Widget Configuration Settings************************///
if (this.datasource[config.datasourceAppSettings].length > 0) {
    //console.log(config);
    for (var i = 0; i < this.datasource[config.datasourceAppSettings].length; i++) {
        //Handle Array Objects for properties
        if (this.datasource[config.datasourceAppSettings][i].data.property_value[0] === "[")
            window.config[this.datasource[config.datasourceAppSettings][i].data.property_name] = JSON.parse(this.datasource[config.datasourceAppSettings][i].data.property_value);
        else
            window.config[this.datasource[config.datasourceAppSettings][i].data.property_name] = this.datasource[config.datasourceAppSettings][i].data.property_value;
    }
}

function init() {
    //Setup Mapbox Control
    if ($("#" + config.targetContainer)[0].innerHTML.length > 0) {
        l=JSON.parse(window.datasources[window.config.datasourceMarkerLocations].latestData());
        l.lat=l.location.latitude;
        l.long=l.location.longitude;
        window.addMarker(l);
        return;
    }
    window.disableMapUpdates = true;
    delete window.markerList;
    mapboxgl.accessToken = config.mapbox_api_key;
    // hardcode this to prevent wierd sync issues with datassources
    mapboxgl.accessToken = "pk.eyJ1IjoicnJlaW5vbGQiLCJhIjoiY2lwOTllOGQ2MDB4Y2VhbnJnMWtzcW9qaiJ9.1mNTDQ5rwabgUL9onzaLxg";
    window.mapObj = new mapboxgl.Map({
        style: 'mapbox://styles/mapbox/streets-v10',
        zoom: config.finalzoom,
        pitch: config.mappitch,
        bearing: 0,
        hash: true,
        container: config.targetContainer,
        center: config.initialcenter
    });
    mapObj.setCenter(config.initialcenter);
    mapObj.setZoom(config.initialzoom);
    mapObj.addControl(new mapboxgl.FullscreenControl());
    mapObj.addControl(new mapboxgl.NavigationControl());
    mapObj.addControl(new mapboxgl.ScaleControl());

    window.mapDraw = new MapboxDraw({
        displayControlsDefault: false,
        controls: {
            polygon: false,
            trash: false
        }
    });
    mapObj.addControl(mapDraw);
    mapObj.on('draw.create', storeGeoFence);
    mapObj.on('draw.delete', window.deleteGeoFence);
   // mapObj.on('loaded', window.mapFlyEffect());
    mapObj.on('loaded', window.disableMapUpdates = false);
    mapObj.on('loaded', window.drawBaseMarkers());
    //mapObj.on('load', window.setTimeout(function(){ window.extrude3DBuildings()}, 2000));

    updateZones();

}

window.enableDraw = function() {
    window.mapDraw.deleteAll();
    if (window.disableMapUpdates)
        window.mapDraw.changeMode('draw_polygon');
    else {
        window.mapDraw.changeMode('simple_select');
    }
}

window.drawBaseMarkers = function() {
    //Add the initial markers
    if (typeof(window.markerList) === "undefined") {
        window.markerList = [];
        ld = window.datasources[config.datasourceNameDevices].latestData().results.DATA;
        for (var i = 0; i < ld.length; i++) {
            addMarker(ld[i]);
        }
    }
}

window.addMarker = function(ds) {
    if (markerList[ds.imei]) {
        markerList[ds.imei].setLngLat([ds.long, ds.lat]);
    } else {
        var el = document.createElement('div');
        // create the popup
        var popup = new mapboxgl.Popup()
            .setText(ds.lastname + "," + ds.firstname);
        el.style = "background-image: url(" + ds.icon + ");background-size: cover;width: 24px;height: 24px;border-radius: 50%;cursor: pointer;";
        var marker = new mapboxgl.Marker(el)
            .setLngLat([ds.long, ds.lat])
            .setPopup(popup)
            .addTo(mapObj);
        markerList[ds.imei] = marker;
    }
};

window.mapFlyEffect = function() {
    window.mapObj.flyTo({
        center: config.finalcenter,
        zoom: config.finalzoom,
        pitch: config.mappitch,
        bearing: 0,
        speed: config.transitionspeed, // make the flying slow
        curve: 1, // change the speed at which it zooms out
        easing: function(t) {
            return t;
        }
    });
};



//****************************************
//Zone Manager Widget
//****************************************
window.storeGeoFence = function(polygon) {
    $("#saveGeoFence").removeClass('disabled')
        .prop('disabled', false)
        .show();
    window.polygon = polygon;
}

window.saveGeoFence = function() {
    var fenceName = "";
    if (document.getElementById("fencename").value === "")
        fenceName = document.getElementById("fencename").placeholder;
    else
        fenceName = document.getElementById("fencename").value;
    window.polygon.features[0].properties.name = fenceName;
    window.datasources[config.datasourceMQTTPolygons].sendData(JSON.stringify({ "action": "create", "name": window.polygon.features[0].properties.name, "coordinates": window.window.polygon.features[0].geometry.coordinates[0] }));
    $("#saveGeoFence").addClass('disabled')
        .prop('disabled', true)
        .hide();
    window.enableDraw();
    //Hack to delay for the update to the message queue
    setTimeout(window.updateZones, 1000);
};

window.updateGeoFence = function() {
    fenceName = "";
    window.datasources[config.datasourceMQTTPolygons].sendData(JSON.stringify({ "action": "update", "name": fenceName, "coordinates": window.newCoordinates }));
};

window.removeGeoFence = function(id) {
    console.log("Removing:" + id);
    //Send to Message Queue
    window.datasources[config.datasourceMQTTPolygons].sendData(JSON.stringify({ "action": "delete", "item_id": id }));
    //Get the latest Data
    window.mapObj.removeLayer(id);
    for (var i = 0; i < JSON.parse(window.datasources[config.datasourceListPolygons].latestData()).length; i++) {
        if (JSON.parse(window.datasources[config.datasourceListPolygons].latestData())[i].item_id === id) {
            delete JSON.parse(window.datasources[config.datasourceListPolygons].latestData())[i];
            break;
        }
    }
    //Hack to delay for the update to the message queue
    setTimeout(window.updateZones, 1000);
};

window.updateZones = function() {
    if (typeof(window.datasources[config.datasourceListPolygons].latestData().length) === "undefined") {
        window.datasources[config.datasourceMQTTPolygons].sendData(JSON.stringify({ "action": "read" }));
        setTimeout(window.updateZones, 1000);
        return;
    } else if (!window.mapObj.loaded()) {
        setTimeout(window.updateZones, 500);
        return;
    }
    updateZoneTable();
    drawZones();

};


window.updateZoneTable = function() {
    ds = JSON.parse(window.datasources[config.datasourceListPolygons].latestData());
    //Reset Table
    document.getElementById("zone_ParentTable").innerHTML = "";
    for (var i = 0; i < ds.length; i++) {
        var row = document.createElement("div");
        row.setAttribute("class", "row");
        row.setAttribute("style", "background-color:" + ds[i].color);
        row.style.marginTop="4px";
        var cell = document.createElement("div");
        cell.setAttribute("class", "col-sm-7");
        cell.innerHTML = ds[i].name;
        row.appendChild(cell);
        cell2 = cell.cloneNode(true);
        cell2.setAttribute("class", "col-sm-5");
        cell2.innerHTML = '<button type="button" class="btn btn-sm trash float-right" onclick=window.removeGeoFence("' + ds[i].item_id + '");></button>';
        row.appendChild(cell2);
        document.getElementById("zone_ParentTable").appendChild(row);
    }
}

window.drawZones = function(ds) {
    ds = JSON.parse(window.datasources[config.datasourceListPolygons].latestData());
    var popup = new mapboxgl.Popup({
        closeButton: false,
        closeOnClick: false
    });
    for (var i = 0; i < ds.length; i++) {
        if (typeof(window.mapObj.getLayer(ds[i].item_id)) === "undefined") {
            var properties = {
                name: ds[i].name
            };
            window.mapObj.addLayer(generatePolygon(ds[i].item_id, properties, [JSON.parse(ds[i].polygon)], ds[i].color, ds[i].opacity / 100));
            mapObj.on('mouseenter', ds[i].item_id, function(e) {
                // Change the cursor style as a UI indicator.
                mapObj.getCanvas().style.cursor = 'pointer';
                // Populate the popup and set its coordinates
                // based on the feature found.
                popup.setLngLat(e.lngLat)
                    .setHTML(e.features[0].properties.name)
                    .addTo(mapObj);
            });

            mapObj.on('mouseleave', ds[i].item_id, function() {
                mapObj.getCanvas().style.cursor = '';
                popup.remove();
            });
        }
    }
}

