window.generatePolygon = function(id, properties, coordinates, color, opacity) {
    if (!opacity)
        opacity = 0.2;
    if (!color)
        color = "#088";
    return {
        'id': id,
        'type': 'fill',
        'source': {
            'type': 'geojson',
            'data': {
                'type': 'Feature',
                'properties': properties,
                'geometry': {
                    'type': 'Polygon',
                    'coordinates': coordinates
                }
            }
        },
        'layout': {},
        'paint': {
            'fill-color': color,
            'fill-opacity': opacity
        }
    };
}

